create table groups (group_id int8 generated by default as identity, group_title varchar(255) not null, primary key (group_id));

create table user_data (username varchar(255) not null, name varchar(255) not null, password varchar(255) not null, primary key (username));

create table group_user (group_id int8 not null, username varchar(255) not null);

create table expenses (id int8 generated by default as identity, amount float8 not null, currency varchar(255) not null, date_created int8, description varchar(255) not null, e_group int8, e_user varchar(255), primary key (id));
